// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title SupplychainValidator cotnract
 * @author 0xJonaseb11
 * It handles all the logic related to validating the activites done in the supplychain to enforce transparency and integrity
 * Helps to make sure that no bad actors done or corruption involved in the chain
 */

import { ERC721 } from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import { RolesManager } from "./RolesManager.sol";
import { MineralRegistry } from "../modules/MineralRegistry.sol";
import { PrivacyGuard } from "../core/PrivacyGuard.sol";
import { Tokenization } from "../tokens/Tokenization.sol";
import { TransactionLog } from "../logs/TransactionLog.sol";
import { LogisticsManager } from "../modules/LogisticsManager.sol";

contract SupplychainValidator is RolesManager {
    RolesManager private rolesManager;
    MineralRegistry private mineralRegistry;
    PrivacyGuard private privacyGuard;
    Tokenization private tokenization;
    TransactionLog private transactionLog;
    LogisticsManager private logisticsManager;

    /**
     * @dev Events for traceability
     */
    event OperationValidated(address indexed actor, string operation, uint256 timestamp);
    event SupplychainValidated(address indexed validator, bool isValid, string mineralId);

    modifier onlyAuthorizedActor(bytes32 role) {
        if (!hasRole(role, msg.sender)) {
            revert InsufficientPermissionsToPerformAction(msg.sender);
        }
        _;
    }

    constructor(
        address _rolesManager,
        address _mineralRegistry,
        address _privacyGuard,
        address _tokenization,
        address _transactionLog,
        address _logisticsManager
    ) RolesManager() {
        rolesManager = RolesManager(_rolesManager);
        mineralRegistry = MineralRegistry(_mineralRegistry);
        privacyGuard = PrivacyGuard(_privacyGuard);
        tokenization = Tokenization(_tokenization);
        transactionLog = TransactionLog(_transactionLog);
        logisticsManager = LogisticsManager(_logisticsManager);
    }

    /*//////////////////////////////////////////////////////////////
        ===============================================================
                              SUPPLYCHAIN VALIDATION PORTAL
        ===============================================================
        //////////////////////////////////////////////////////////////*/

    /*/////////////////////////////////////////
                              MINER
            /////////////////////////////////////////*/

    /**
     * @dev validates mining operation
     * @notice Emits OperationValidated event fon successful operation validation
     */
    function validateMiningOperation(
        string memory mineralId
    ) external onlyAuthorizedActor(MINER_ROLE) onlyValidMineral(mineralId) {
        transactionLog.recordOperation(msg.sender, "Mining", mineralId);

        emit OperationValidated(msg.sender, "Mining", block.timestamp);
    }

    /*/////////////////////////////////////////
                              REFINER
            /////////////////////////////////////////*/

    /**
     * @dev validates refining operation
     * @notice Emits OperationValidated even on successful validation
     */
    function validateRefiningOperation(
        string memory mineralId
    ) external onlyAuthorizedActor(REFINER_ROLE) onlyValidMineral(mineralId) {
        transactionLog.recordOperation(msg.sender, "Refining", mineralId);

        emit OperationValidated(msg.sender, "Refining", block.timestamp);
    }

    /*/////////////////////////////////////////
                              TRASPORTER
            /////////////////////////////////////////*/

    /**
     * @dev validates transportation operation
     * @notice Emits OperationValidated even on successful validation
     */
    function validateTransportationOperation(
        string memory mineralId,
        address to
    ) external onlyAuthorizedActor(TRANSPORTER_ROLE) onlyValidMineral(mineralId) {
        uint256 tokenId = tokenization.getTokenIdByMineralId(mineralId);

        tokenization.safeTransferFrom(msg.sender, to, tokenId);

        transactionLog.recordOperation(msg.sender, "Transportation", mineralId);

        emit OperationValidated(msg.sender, "Transportation", block.timestamp);
    }

    /*/////////////////////////////////////////
                              BUYER
            /////////////////////////////////////////*/

    /**
     * @dev validate purchase operation
     * @notice Emits OperationValidated event on success validation
     */
    function validatePurchaseOperation(
        string memory mineralId,
        address buyer
    ) external onlyAuthorizedActor(BUYER_ROLE) onlyValidMineral(mineralId) {
        uint256 tokenId = tokenization.getTokenIdByMineralId(mineralId);

        tokenization.safeTransferFrom(msg.sender, buyer, tokenId);
        transactionLog.recordOperation(msg.sender, "Purchase", mineralId);

        emit OperationValidated(msg.sender, "Purchase", block.timestamp);
    }

    /*/////////////////////////////////////////
                              INSPECTOR
            /////////////////////////////////////////*/

    /**
     * @dev validates Inspection operation
     * @notice Emits OperationValidated event on successfl validataion
     */
    function validateInspectionOperation(
        string memory mineralId,
        address inspector
    ) external onlyAuthorizedActor(INSPECTOR_ROLE) onlyValidMineral(mineralId) {
        transactionLog.recordOperation(inspector, "Inspection", mineralId);

        emit OperationValidated(inspector, "Inspector", block.timestamp);
    }

    /**
     * @dev Validates the supply chain of a mineral
     * @param mineralId The ID of the mineral to validate
     * @return isValid Indicates whether the supply chain is valid
     * @return validationReason A string describing the validation result
     */

    function validateSupplychain(
        string memory mineralId
    ) public returns (bool isValid, string memory validationReason) {
        MineralRegistry.MineralDetails memory details = mineralRegistry.getMineralDetails(mineralId);

        (bool isAudited, bool isInspected) = mineralRegistry.checkAuditAndInspectionStatus(mineralId);

        if (!isAudited) {
            return (false, "Mineral is not audited!!");
        }
        if (!isInspected) {
            return (false, "Mineral is not inspected!!");
        }

        // check for mineral transaction history
        TransactionLog.Transaction[] memory transactions = transactionLog.getTransactionsByMineral(mineralId);
        if (transactions.length == 0) {
            // revert SupplychainValidator__InvalidMineralTransactionHistory();
            return (false, "No transaction history found for specified mineral");
        }

        // validate ownership chain in transactions
        address currentOwner = details.currentHandler;
        for (uint256 i = transactions.length; i > 0; i--) {
            TransactionLog.Transaction memory transaction = transactions[i - 1];
            if (transaction.receiver != currentOwner) {
                // revert SupplychainValidator__InvalidMineralOwnershipChain();
                return (false, "Invalid ownership chain in transactions!!");
            }
            currentOwner = transaction.sender;
        }

        if (currentOwner != details.registeredBy) {
            // revert SupplychainValidator__InvalidMineralOwnershipChain();
            return (false, "Ownership chain does not lead back to the original registrant!!");
        }

        // validation passed
        return (true, "Supplychain is Valid!!");
    }

    /**
     * @dev Logs validation results for specified mineral
     * @notice Emits SupplychainValidated, an event with the validation reason of the mineral supplychain
     * @param mineralId The ID of the mineral to validate
     */
    function logValidationResult(string memory mineralId) public returns (bool, string memory) {
        if (bytes(mineralId).length == 0 || !isMineralRegistered(mineralId)) {
            revert SupplychainValidator__InvalidMineralIdOrNotFound(mineralId);
        }
        (bool isValid, string memory validationReason) = validateSupplychain(mineralId);

        emit SupplychainValidated(msg.sender, isValid, mineralId);

        return (isValid, validationReason);
    }
}

contracts/governance/DisputeResolution.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title DisputeResolution contract
 * @author @0xJonaseb11
 * @dev It handles and resolves disputes raised by supplychain members
 * @dev It incorporates weighted voting to make sure that disputes are resolved in transparency
 * @notice Hard-to-resolve disputes are later on decided by the super admins for resolution
 */

import { MineralRegistry } from "../modules/MineralRegistry.sol";
import { TransactionLog } from "../logs/TransactionLog.sol";
import { RolesManager } from "../core/RolesManager.sol";

contract DisputeResolution is RolesManager {
    RolesManager private rolesManager;

    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event DisputeRaised(
        uint256 indexed disputeId,
        string mineralId,
        address indexed complainant,
        address defendant,
        string details,
        uint256 raisedAt
    );
    event DisputeResolved(uint256 indexed disputeId, string resolutionDetails, uint256 timestamp);
    event DisputeClosedUnresolved(
        address closedBy,
        uint256 disputeId,
        string mineralId,
        address complainant,
        address defendant,
        string details,
        uint256 closedUnresolvedAt
    );
    event DisputeEscalated(uint256 indexed disputeId, uint256 timestamp);
    event DisputeRejected(uint256 indexed disputeId, string reason, uint256 timestamp);

    /*//////////////////////////////////////////////////////////////
                            ENUM FOR STATUS
    //////////////////////////////////////////////////////////////*/
    enum DisputeStatus {
        Pending,
        Resolved,
        Rejected,
        Escalated
    }

    struct Dispute {
        uint256 disputeId;
        string mineralId;
        address complainant;
        address defendant;
        string details;
        string evidence;
        DisputeStatus status;
        uint256 createdAt;
        uint256 resolvedAt;
        uint256 approveVotes;
        uint256 rejectVotes;
        mapping(address => bool) hasVoted;
        uint256 votingDeadline;
    }

    mapping(uint256 => Dispute) public disputes;
    uint256 public disputeCount;
    uint256 public votingThreshold = 3; // Minimum votes required for resolution
    uint256 public votingDuration = 10 days;

    /**
     * @dev Events to enforce traceability
     */

    constructor() {}

    modifier onlyValidDisputeId(uint256 disputeId) {
        if (disputeId == 0 || disputes[disputeId].disputeId != disputeId)
            revert DisputeResolution__InvalidDisputeIdOrNotFound();
        _;
    }

    /**
     * @dev Registers a dispute
     * @param mineralId The ID of the mineral in question
     * @param defendant The address of the party being accused
     * @param details The details of the dispute
     * @param evidence Evidence supporting the dispute
     */
    function raiseDispute(
        string memory mineralId,
        address defendant,
        string calldata details,
        string calldata evidence
    ) external {
        if (bytes(mineralId).length == 0) revert DisputeResolution__InvalidMineralIdOrNotFound();
        if (defendant == address(0)) revert DisputeResolution__InvalidDisputeDefendantAddress();
        if (bytes(details).length == 0) revert DisputeResolution__InvalidDisputeDetails();
        if (bytes(evidence).length == 0) revert DisputeResolution__InvalidDisputeEvidence();

        uint256 disputeId = disputeCount++;

        Dispute storage dispute = disputes[disputeId];
        dispute.disputeId = disputeId;
        dispute.mineralId = mineralId;
        dispute.complainant = msg.sender;
        dispute.defendant = defendant;
        dispute.details = details;
        dispute.evidence = evidence;
        dispute.status = DisputeStatus.Pending;
        dispute.createdAt = block.timestamp;
        dispute.resolvedAt = 0;
        dispute.approveVotes = 0;
        dispute.rejectVotes = 0;
        dispute.votingDeadline = block.timestamp + votingDuration;

        emit DisputeRaised(disputeId, mineralId, msg.sender, defendant, details, block.timestamp);
    }

    /**
     * @dev Voting mechanism for resolving disputes
     * @param disputeId The ID of the disputet to vote on
     * @param approve Whether the voter approves resoluction or rejects it
     */

    function voteOnDispute(uint256 disputeId, bool approve) external {
        Dispute storage dispute = disputes[disputeId];

        if (dispute.status != DisputeStatus.Pending) revert DisputeResolution__DisputeStatusNotPending(disputeId);
        if (dispute.hasVoted[msg.sender]) revert DisputeResolution__AlreadyVoted(msg.sender);
        if (
            !hasRole(AUDITOR_ROLE, msg.sender) &&
            !hasRole(INSPECTOR_ROLE, msg.sender) &&
            !hasRole(DEFAULT_ADMIN_ROLE, msg.sender)
        ) {
            revert DisputeResolution__NotEligibleToVote(msg.sender);
        }

        /**
         * check if voting period has expired, if yes -> Auto escalate
         */
        if (block.timestamp >= dispute.votingDeadline) {
            dispute.status = DisputeStatus.Escalated;

            emit DisputeEscalated(disputeId, block.timestamp);
            revert DisputeResolution__DisputeEscalated_UnableToVote(disputeId);
        }

        dispute.hasVoted[msg.sender] == true;

        if (approve) {
            dispute.approveVotes++;
        } else {
            dispute.rejectVotes++;
        }

        if (dispute.approveVotes >= votingThreshold) {
            dispute.status = DisputeStatus.Resolved;
            dispute.resolvedAt = block.timestamp;

            emit DisputeResolved(disputeId, "Approved by majority voting", block.timestamp);
        } else if (dispute.rejectVotes >= votingThreshold) {
            dispute.status = DisputeStatus.Rejected;
            emit DisputeRejected(disputeId, "Rejected by majority voting", block.timestamp);
        }
    }

    /**
     * @dev Resolves a dispute
     * @param disputeId The ID of the dispute to resolve
     * @param resolutionDetails The details of the resolution
     * @notice Emits DisputeResolved event on successful dispute resolution
     */
    function resolveDispute(
        uint256 disputeId,
        string calldata resolutionDetails
    ) external restrictedToRole(DEFAULT_ADMIN_ROLE) {
        Dispute storage dispute = disputes[disputeId];

        if (dispute.disputeId != disputeId) revert DisputeResolution__InvalidDisputeIdOrNotFound();
        if (bytes(resolutionDetails).length == 0) revert DisputeResolution__InvalidResolutionDetails();
        if (dispute.status != DisputeStatus.Pending || dispute.status != DisputeStatus.Escalated)
            revert DisputeResolution__DisputeStatusNotPendingOrEscalated();

        dispute.status = DisputeStatus.Resolved;
        dispute.resolvedAt = block.timestamp;

        emit DisputeResolved(disputeId, resolutionDetails, block.timestamp);
    }

    function resolveEscalatedDisputes(
        uint256 disputeId,
        string memory resolutionDetails
    ) public restrictedToRole(DEFAULT_ADMIN_ROLE) {
        Dispute storage dispute = disputes[disputeId];

        if (dispute.status != DisputeStatus.Escalated) revert DisputeResolution__DisputeNotEscalated(disputeId);

        dispute.status = DisputeStatus.Resolved;
        dispute.resolvedAt = block.timestamp;

        emit DisputeResolved(disputeId, resolutionDetails, block.timestamp);
    }

    /*//////////////////////////////////////////////////////////////
                             REJECT DISPUTE 
        //////////////////////////////////////////////////////////////*/

    /*//////////////////////////////////////////////////////////////
                            ESCALATE DISPUTE
        //////////////////////////////////////////////////////////////*/

    /**
     * @dev Anyone with role can call this to manually trigger escalation for expired disputes
     * @dev Escalates a dispute to the super admin for final admin
     */
    function checkAndEscalateDispute(uint256 disputeId) external {
        Dispute storage dispute = disputes[disputeId];

        if (dispute.status != DisputeStatus.Pending) revert DisputeResolution__DisputeStatusNotPending(disputeId);

        if (!hasRole(AUDITOR_ROLE, msg.sender) && !hasRole(INSPECTOR_ROLE, msg.sender)) {
            revert DisputeResolution__NotEligibleToVote(msg.sender);
        }

        if (block.timestamp < dispute.votingDeadline) revert DisputeResolution__VotingPeriodStillActive();

        dispute.status = DisputeStatus.Escalated;

        emit DisputeEscalated(disputeId, block.timestamp);
    }
}

