// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title MineralRegistry contract
 * @author @0xJonaseb11
 * @dev It is a mineral factory contract. It is where new minerals are registered, updated and gotten till next step
 * @dev Minerals are gotten from mineral registry contract and all other operations proceed right from there!!
 * @notice It is the base contract of the supplychain
 */

import { RolesManager } from "../core/RolesManager.sol";

contract MineralRegistry is RolesManager {
    RolesManager private rolesManager;
    // uint256 private nextMineralId = 1;

    // Events for transparency
    event MineralUpdated(
        string mineralId,
        string updatedField,
        string newValue,
        address indexed updatedBy,
        uint256 updatedAt
    );
    event MineralLocationUpdated(
        string mineralId,
        string previousLocation,
        string newLocation,
        address indexed tranporter
    );

    /**
     * @dev connects RolesManager contract
     */
    constructor(address rolesManagerAddress) {
        rolesManager = RolesManager(rolesManagerAddress);
    }

    /**
     * @dev Restrict actions to specific roles
     */
    modifier onlyAuthorized(bytes32 role) {
        if (!hasRole(role, msg.sender)) {
            revert InsufficientPermissionsToPerformAction(msg.sender);
        }
        _;
    }

    /**
     * @dev Updates mineral details - only by authorized roles
     * @param mineralId The ID of the mineral under update
     * @param newStatus New status of the mineral
     * @notice Emits MineralUpdated event on successful updation
     */
    function updateMineralStatus(string memory mineralId, string memory newStatus) public {
        if (
            keccak256(bytes(mineralDetails[mineralId].id)) != keccak256(bytes(mineralId)) ||
            bytes(mineralId).length == 0
        ) {
            revert MineralRegistry__InvalidMineralIdOrNotFound(mineralId);
        }
        if (bytes(newStatus).length == 0) revert MineralRegistry__InvalidMineralStatus();

        if (
            !hasRole(REFINER_ROLE, msg.sender) ||
            !hasRole(TRANSPORTER_ROLE, msg.sender) ||
            !hasRole(AUDITOR_ROLE, msg.sender) ||
            !hasRole(INSPECTOR_ROLE, msg.sender)
        ) {
            revert InsufficientPermissionsToPerformAction(msg.sender);
        }

        mineralDetails[mineralId].currentStatus = newStatus;
        mineralDetails[mineralId].timestamp = block.timestamp;

        // record mineral history
        mineralHistories[mineralId].push(
            MineralHistory({
                id: mineralId,
                fieldChanged: "Status",
                newValue: newStatus,
                updatedBy: msg.sender,
                timestamp: block.timestamp
            })
        );

        emit MineralUpdated(mineralId, "Status", newStatus, msg.sender, block.timestamp);
    }

    /*////////////////////////////////////////////////////////////
        =============================================================
                       TRANSPORTATION ENTERPRISE
        =============================================================               
        ////////////////////////////////////////////////////////////*/

    /**
     * @dev updates the location of the mineral
     * @param mineralId the ID of the mineral to update
     * @param newLocation The new location of the mineral
     * @notice Emits MineralLocationUpdated event on successful location update!
     */
    function updateMineralLocation(
        string memory mineralId,
        string memory newLocation
    ) public onlyAuthorized(TRANSPORTER_ROLE) {
        // custom error handling

        if (
            keccak256(bytes(mineralDetails[mineralId].id)) != keccak256(bytes(mineralId)) ||
            bytes(mineralId).length == 0
        ) {
            revert MineralRegistry__InvalidMineralIdOrNotFound(mineralId);
        }

        if (bytes(newLocation).length == 0) {
            revert MineralRegistry__InvalidMineralLocation();
        }

        string memory previousLocation = mineralDetails[mineralId].currentLocation;
        mineralDetails[mineralId].currentLocation = newLocation;

        emit MineralLocationUpdated(mineralId, previousLocation, newLocation, msg.sender);
    }

    /*//////////////////////////////////////////////////////////////
        ===============================================================
                     SUPPLYCHAIN VALIDATION PORTAL
        ===============================================================
        //////////////////////////////////////////////////////////////*/

    /**
     * @dev retrieves mineral details of specified mineralId
     * @return mineral details of specified mineralId
     */
    function getMineralDetails(string memory mineralId) public view virtual returns (MineralDetails memory) {
        if (
            keccak256(bytes(mineralDetails[mineralId].id)) != keccak256(bytes(mineralId)) ||
            bytes(mineralId).length == 0
        ) {
            revert MineralRegistry__InvalidMineralIdOrNotFound(mineralId);
        }
        return mineralDetails[mineralId];
    }

    /**
     * @dev retrieves fll history of a mineral (for audit retail)
     * @return history of specified mineral sing its mineralId
     */
    function _getMineralHistory(string memory mineralId /*override*/) public view returns (MineralHistory[] memory) {
        if (
            keccak256(bytes(mineralDetails[mineralId].id)) != keccak256(bytes(mineralId)) ||
            bytes(mineralId).length == 0
        ) {
            revert MineralRegistry__InvalidMineralIdOrNotFound(mineralId);
        }
        return mineralHistories[mineralId];
    }

    /**
     * @dev checks if a mineral with the given ID is registered
     * @param mineralId the ID of the mineral to check
     * @return A boolean indicating whether the mineral is registered or not
     */

    function isMineralAudited(string memory mineralId) public view returns (bool) {
        if (
            keccak256(bytes(mineralDetails[mineralId].id)) != keccak256(bytes(mineralId)) ||
            bytes(mineralId).length == 0
        ) {
            revert MineralRegistry__InvalidMineralIdOrNotFound(mineralId);
        }

        return mineralDetails[mineralId].isAudited;
    }

    function isMineralInspected(string memory mineralId) public view returns (bool) {
        if (
            keccak256(bytes(mineralDetails[mineralId].id)) != keccak256(bytes(mineralId)) ||
            bytes(mineralId).length == 0
        ) {
            revert MineralRegistry__InvalidMineralIdOrNotFound(mineralId);
        }

        return mineralDetails[mineralId].isInspected;
    }

    /*///////////////////////////////////////////////
                         AUDITOR
        ///////////////////////////////////////////////*/

    /**
     * @dev enables an auditor to audit a mineral
     * @notice Emits MineralAudited event on successful auditing of a mineral
     */

    /*///////////////////////////////////////////////////////
                          INSPECTOR
        ///////////////////////////////////////////////////////*/

    /**
     * @dev enables only the inspector to audit a mineralDetails
     * @notice Emits MineralInspected even
     */

    /**
     * @dev checks audit and inspection status
     * Returns the status of both audit and inspection of mineral according to mineralId
     */
    function _checkAuditAndInspectionStatus(
        string memory mineralId
    ) public /*override*/ onlyAuthorized(DEFAULT_ADMIN_ROLE) returns (bool isAudited, bool isInspected) {
        if (
            keccak256(bytes(mineralDetails[mineralId].id)) != keccak256(bytes(mineralId)) ||
            bytes(mineralId).length == 0
        ) {
            revert MineralRegistry__InvalidMineralIdOrNotFound(mineralId);
        }

        if (mineralDetails[mineralId].isAudited && mineralDetails[mineralId].isInspected) {
            emit MineralReadyToTrade(mineralId, msg.sender, "TradingReady", block.timestamp);
        } else {
            revert MineralNotReadyToTrade(mineralId);
        }

        // explicitly return audit and inspection results
        return (mineralDetails[mineralId].isAudited, mineralDetails[mineralId].isInspected);
    }

    /*//////////////////////////////////////////////////////////////
                            HELPER FUNCTIONS
        //////////////////////////////////////////////////////////////*/

    /**
     * @dev helper function to convert address to string
     * @return string of address
     */
    function addressToString(address _address) internal pure returns (string memory) {
        bytes32 value = bytes32(uint256(uint160(_address)));
        bytes memory characters = "ABACDEFGHIJ1245678909876";

        bytes memory str = new bytes(42);
        str[0] = "0";
        str[1] = "x";

        for (uint256 i = 0; i < 20; i++) {
            str[2 + i * 2] = characters[uint8(value[i + 12] >> 4)];
            str[3 + i * 2] = characters[uint8(value[i + 12] & 0x0f)];
        }

        return string(str);
    }
}

contracts/modules/MineralWarehouse.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @author @0xJonaseb11
 * title MineralWarehouse contract
 * @dev It is a mineral warehouse contract. Minerals are stored here after refinery and can be listed for sale
 * @notice It is the base contract of the supplychain
 */
import { RolesManager } from "../core/RolesManager.sol";
import { MineralRegistry } from "../modules/MineralRegistry.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";

contract MineralWarehouse is RolesManager /*,MineralRegistry*/ {
    // MineralRegistry private mineralRegistry;
    RolesManager private rolesManager;

    enum PaymentMethod {
        ETH,
        TOKEN
    }

    struct MineralSaleInfo {
        string price;
        uint256 priceETH;
        mapping(address => uint256) tokenPrices;
    }

    struct StoredMineral {
        string mineralId;
        address refiner;
        uint256 storedAt;
        uint256 soldAt;
        bool isStored;
        bool isSold;
        bool isForSale;
        address buyer;
        bool isMarketReady;
        MineralSaleInfo saleInfo;
    }

    mapping(string => StoredMineral) private warehouse;
    string[] private storedMineralIds;
    mapping(address => bool) public acceptedTokens;

    event MineralStored(string mineralId, address indexed refiner, uint256 storedAt);
    event MineralListedForSale(string mineralId, string price, address indexed lister);
    event MineralSold(string mineralId, address indexed buyer, address seller, uint256 soldAt);

    constructor(
        address rolesManagerAddress /*, address mineralRegistryAddress*/
    ) /*MineralRegistry(rolesManagerAddress) */ {
        rolesManager = RolesManager(rolesManagerAddress);
        // mineralRegistry = MineralRegistry(mineralRegistryAddress);
    }

    function addAcceptedToken(address _token) external restrictedToRole(DEFAULT_ADMIN_ROLE) {
        require(_token != address(0), "Invalid token address");
        acceptedTokens[_token] = true;
    }

    function store_refined_mineral_to_warehouse(
        string memory _mineralId,
        uint256 _priceETH,
        address[] memory tokens,
        uint256[] memory prices
    ) public restrictedToRole(REFINER_ROLE) {
        if (mineralDetails[_mineralId].isRefined == false) {
            revert MineralWarehouse__MineralNotRefined(_mineralId);
        }

        if (tokens.length != prices.length) {
            revert MineralWarehouse__MismatchedArrays();
        }

        if (_priceETH <= 0) {
            revert MineralWarehouse__InvalidETHPrice();
        }
        require(mineralDetails[_mineralId].isRefined, "Mineral not refined");
        require(tokens.length == prices.length, "Mismatched arrays");
        require(_priceETH > 0, "Invalid ETH price");

        StoredMineral storage mineral = warehouse[_mineralId];
        mineral.mineralId = _mineralId;
        mineral.refiner = msg.sender;
        mineral.isStored = true;
        mineral.storedAt = block.timestamp;
        mineral.isMarketReady = true;
        mineral.saleInfo.priceETH = _priceETH;

        for (uint256 i = 0; i < tokens.length; i++) {
            require(acceptedTokens[tokens[i]], "Token not accepted");
            mineral.saleInfo.tokenPrices[tokens[i]] = prices[i];
        }

        storedMineralIds.push(_mineralId);
        emit MineralStored(_mineralId, msg.sender, block.timestamp);
    }

    function list_Mineral_ForSale(
        string memory _mineralId,
        string memory _price
    ) public restrictedToRole(REFINER_ROLE) {
        StoredMineral storage mineral = warehouse[_mineralId];
        require(mineral.isStored, "Mineral not stored");
        require(!mineral.isSold, "Already sold");
        require(msg.sender == mineral.refiner, "Not owner");
        require(mineral.isMarketReady, "Not market ready");

        mineral.isForSale = true;
        mineral.saleInfo.price = _price;
        emit MineralListedForSale(_mineralId, _price, msg.sender);
    }

    function purchase_mineral(
        string memory _mineralId,
        PaymentMethod method,
        address token,
        uint256 amount
    ) public payable restrictedToRole(BUYER_ROLE) {
        StoredMineral storage mineral = warehouse[_mineralId];

        // require(mineral.isStored, "Mineral not found");
        require(!mineral.isSold, "Already sold");
        // require(mineral.isForSale, "Not for sale");
        // require(mineral.isMarketReady, "Not market ready");

        if (method == PaymentMethod.ETH) {
            require(msg.value == mineral.saleInfo.priceETH, "Incorrect ETH amount");
            payable(mineral.refiner).transfer(msg.value);
        } else {
            // require(acceptedTokens[token], "Token not accepted");

            require(amount == mineral.saleInfo.tokenPrices[token], "Incorrect token amount");
            IERC20(token).transferFrom(msg.sender, mineral.refiner, amount);
        }

        mineral.isSold = true;
        mineral.isForSale = false;
        mineral.soldAt = block.timestamp;
        mineral.buyer = msg.sender;

        emit MineralSold(_mineralId, msg.sender, mineral.refiner, block.timestamp);
    }

    function getMineralPriceInToken(string memory _mineralId, address token) public view returns (uint256) {
        if (acceptedTokens[token] == false) {
            revert MineralWarehouse__TokenNotAccepted(token);
        }
        return warehouse[_mineralId].saleInfo.tokenPrices[token];
    }

    function isMineralMarketReady(string memory _mineralId) public view returns (bool) {
        return warehouse[_mineralId].isMarketReady;
    }
}

contracts/tokens/Tokenization.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { ERC721 } from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { RolesManager } from "../core/RolesManager.sol";

contract Tokenization is ERC721, RolesManager {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER");

    struct MineralToken {
        string mineralId;
        string details;
        address owner;
        uint8 flags; // bit 0: minted, 1: audited, 2: inspected, 3: purchased
    }

    uint256 private nextTokenId = 1;
    mapping(uint256 => MineralToken) private _tokens;
    mapping(string => uint256) public mineralToToken;

    event Minted(uint256 indexed tokenId, string mineralId, address indexed to);
    event Transferred(uint256 indexed tokenId, address indexed from, address indexed to);
    event Updated(uint256 indexed tokenId);
    event Revoked(uint256 indexed tokenId, string reason);
    event MetadataUpdated(uint256 indexed tokenId);

    constructor() ERC721("MineralToken", "MTKN") {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);
    }

    // Remove the onlyRole modifier since it's inherited from AccessControl
    // Keep onlySpecificRole if needed from RolesManager

    function mintToken(address to, string calldata mineralId, string calldata details) external {
        require(hasRole(MINTER_ROLE, msg.sender), "Unauthorized");
        uint256 tokenId = nextTokenId++;

        require(!_exists(tokenId), "Exists");
        require(mineralToToken[mineralId] == 0, "MineralUsed");

        _mint(to, tokenId);

        _tokens[tokenId] = MineralToken({ mineralId: mineralId, details: details, owner: msg.sender, flags: 0x01 });

        mineralToToken[mineralId] = tokenId;
        emit Minted(tokenId, mineralId, to);
    }

    function updateToken(uint256 tokenId, string calldata, bool audited, bool inspected, bool purchased) external {
        address owner = ownerOf(tokenId);
        require(
            owner == msg.sender || getApproved(tokenId) == msg.sender || isApprovedForAll(owner, msg.sender),
            "NotOwner"
        );

        MineralToken storage t = _tokens[tokenId];
        t.flags = _setFlag(t.flags, 1, audited);
        t.flags = _setFlag(t.flags, 2, inspected);
        t.flags = _setFlag(t.flags, 3, purchased);

        emit Updated(tokenId);
    }

    function updateMetadata(uint256 tokenId, string calldata newDetails) external {
        address owner = ownerOf(tokenId);
        require(
            owner == msg.sender || getApproved(tokenId) == msg.sender || isApprovedForAll(owner, msg.sender),
            "NotOwner"
        );
        _tokens[tokenId].details = newDetails;
        emit MetadataUpdated(tokenId);
    }

    function transferOwnership(uint256 tokenId, address newOwner) external {
        require(hasRole(BUYER_ROLE, msg.sender), "Unauthorized");
        address owner = ownerOf(tokenId);
        require(
            owner == msg.sender || getApproved(tokenId) == msg.sender || isApprovedForAll(owner, msg.sender),
            "NotOwner"
        );

        _transfer(owner, newOwner, tokenId);
        _tokens[tokenId].owner = newOwner;

        emit Transferred(tokenId, owner, newOwner);
    }

    function revokeToken(uint256 tokenId, string calldata reason) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Unauthorized");
        require(_exists(tokenId), "Invalid");
        _burn(tokenId);
        delete _tokens[tokenId];
        emit Revoked(tokenId, reason);
    }

    function getTokenDetails(
        uint256 tokenId
    )
        external
        view
        returns (
            uint256 id,
            string memory mineralId,
            string memory details,
            bool isMinted,
            bool isAudited,
            bool isInspected,
            bool isPurchased,
            address owner
        )
    {
        require(_exists(tokenId), "Invalid");
        MineralToken memory t = _tokens[tokenId];
        return (
            tokenId,
            t.mineralId,
            t.details,
            (t.flags & 0x01) != 0,
            (t.flags & 0x02) != 0,
            (t.flags & 0x04) != 0,
            (t.flags & 0x08) != 0,
            t.owner
        );
    }

    function _setFlag(uint8 flags, uint8 bit, bool value) private pure returns (uint8) {
        return value ? uint8(flags | (1 << bit)) : uint8(flags & ~(1 << bit));
    }

    function _exists(uint256 tokenId) internal override view returns (bool) {
        return _tokens[tokenId].owner != address(0);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function getTokenIdByMineralId(string calldata mineralId) external view returns (uint256) {
        uint256 tokenId = mineralToToken[mineralId];
        require(_exists(tokenId), "Invalid");
        return tokenId;
    }
}

